align-items: center;
border-radius: 10px;

@media screen and (max-width: 768px) {
    height: 1100px;
}

@media screen and (max-width: 480px) {
    height: 1300px;
}
`
export const BlogWrapper = styled.div`
max-width: 1500px;
margin: 0 auto;
display: grid;
grid-template-columns: 1fr;
align-items:center;
text-align:center;
grid-gap: 16px;
padding: 0 50px;
color: red;

@media screen and (max-width: 1000px) {
    grid-template-columns: 1fr 
}
@media screen and (max-width: 768px) {
