    padding: 0 20px;
}`

export const BlogTitle = styled.h1`
font-size: 3rem;
color: red;
padding:30px;
font-family: 'Dosis', sans-serif;

@media screen and (max-width: 480px) {
    font-size: 2rem;
    margin-top:50px;
}
@media screen and (max-width: 1000px) {
font-size:3.8rem;
}
`

