import React, { useState } from 'react'
import { Link, Route } from 'react-router-dom'
import ArticleDetail from './ArticleDetail'

function Blog({ data }) {
    //initialise state
    const [state, setState] = useState({
        post: {}
    })

    const fetchSingle = async (id) => {
        let res = await fetch(`https://jsonplaceholder.typicode.com/posts/${id}`)
        let data = await res.json();
        console.log(data)
        setState({
            post: data
        })
    }

    return (
        <div className="wrapper">

            <h1 style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '10vh', color: 'grey' }}>
                Blog page</h1>

            {console.log(data)}
            { data.map((item, i) => {
                return (
                    <Link onClick={() => fetchSingle(item.id)} key={i} to={`/blog/${item.id}`} style={{ 'display': 'block' }}>{item.title}</Link>
                )
            })}
            <Route path='/blog/:id' render={() => <ArticleDetail single={state.post} />} />
        </div>
    )
}
export default Blog
